# quotes/views.py

from django.shortcuts import render
from django.http import HttpResponse
import time
import random

quotes = [
    "To be honest, when I found out the patriarchy wasn't just about horses, I lost interest.",
    "I'm just Ken and I'm enough / And I'm great at doing stuff.",
    "Is it just me or did these mojo dojo casa houses just get a whole lot dreamier?",
    "You guys aren't doing patriarchy very well.",
]
images = [
    "https://static.wikia.nocookie.net/villains/images/a/ac/Ken2023.png/revision/latest?cb=20230724105239",
    "data:image/jpeg;base64,",
    "data:image/jpeg;base64,"
]

# Create your views here.
def quote(request):
    '''function to respond to the /quotes and /quotes/quote urls'''
    template_name = "quotes/quote.html"
    context = {
        "current_time": time.ctime(),
        "quote": random.choice(quotes),
        "image_source": random.choice(images),
    }
    return render(request, template_name=template_name, context=context)

def show_all(request):
    '''function to respond to the /quotes/show_all url'''
    template_name="quotes/show_all.html"
    context = {
        "current_time": time.ctime(),
        "all_quotes": quotes,
        "all_images": images,
    }
    return render(request, template_name=template_name, context=context)

def about(request):
    '''function to respond to the /quotes/about url'''
    template_name="quotes/about.html"
    context = {
        "current_time": time.ctime(),
        "all_quotes": quotes,
        "all_images": images,
    }
    return render(request, template_name=template_name, context=context)